package programming.task1;



class Table {
        public enum Sign {
            SIGN_X("X"),
            SIGN_ZERO("0"),
            SIGN_EMPTY("·");
            private final String x;
             Sign(String x) {
                this.x = x;
            }
        }

        private final int row;
        private final int col;
        private final Sign[][] table;
        public Table(int row, int col) {
            this.row = row;
            this.col = col;
            table = new Sign[row][col];
            for(int i = 0; i < row; i++) {
                for(int j = 0; j < col; j++) {
                    table[i][j] = Sign.SIGN_EMPTY;
                }
            }
        }

        @Override
        public String toString() {
            final StringBuilder tableString = new StringBuilder();
            for (int i = 0; i < row; i++) {
                for (int j = 0; j < col; j++) {
                    tableString.append(table[i][j].x).append("  ");
                }
                tableString.append("\n");
            }
            return tableString.toString();
        }

        //добавление крестика/нолика или очистка клетки
        public void addSign(int row, int col, Sign mark) {
            table[row - 1][col - 1] = mark;
        }

        //возвращает значение заданной клетки
        public Sign getSign(int row, int col) {
            return table[row - 1][col - 1];
        }

        //поиск максимальной последовательности крестиков/ноликов(аналогично)
        public int maxLength(Sign mark) {
            int maxLength = 0;
            int length = 0;

            //поиск по горизонтали
            for (int i = 0; i < row; i++) {
                for (int j = 0; j < col; j++) {
                    if (table[i][j] == mark) {
                        length++;
                        if (length > maxLength) maxLength = length;
                    } else length = 0;
                }
            }

            //поиск по вертикали
            length = 0;
            for (int i = 0; i < col; i++) {
                for (int j = 0; j < row; j++) {
                    if (table[j][i] == mark) {
                        length++;
                        if (length > maxLength) maxLength = length;
                    } else length = 0;
                }
            }

            //поиск по главной диагонали
            length = 0;
            for (int i = 0; i < row; i++) {
                if (table[i][i] == mark) {
                    length++;
                } else {
                    length = 0;
                }
                if (length > maxLength) maxLength = length;
            }

            //поиск по побочной диагонали
            length = 0;
            for (int i = 0; i < row; i++) {
                if (table[i][row - i - 1] == mark) {
                    length++;
                } else {
                    length = 0;
                }
                if (length > maxLength) maxLength = length;
            }

            return maxLength;
        }
}

